select a.category, c.city, x.state, y.region, sum(b.profit) as profit from gbc__superstore.product a, gbc__superstore.sales b, gbc__superstore.customer d, gbc__superstore.location c, gbc__superstore.location x, gbc__superstore.location y where a.Product_ID = b.product_ID and b.customer_ID = d.customer_ID and d.location_ID = c.location_ID and d.location_ID = x.location_ID and d.location_ID = y.location_ID group by a.category, c.city, x.State, y.Region order by profit;